// src/components/InsightsChart.js
import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import { Container, Paper, Typography } from '@mui/material';
import { Chart, registerables } from 'chart.js';
import 'chart.js/auto';
import './InsightsChart.css'; // Assuming you have a CSS file for styling

Chart.register(...registerables);

const InsightsChart = () => {
  const [chartData, setChartData] = useState({});
  const chartContainerRef = useRef(null);
  const chartInstanceRef = useRef(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/insights');
        const insights = response.data;

        // Check if insights is an array before proceeding
        if (Array.isArray(insights)) {
          const sectors = insights.map(insight => insight.sector);
          const intensities = insights.map(insight => insight.intensity);

          const newChartData = {
            labels: sectors,
            datasets: [
              {
                label: 'Intensity',
                data: intensities,
                backgroundColor: 'rgba(75, 192, 192, 0.6)',
              }
            ]
          };

          setChartData(newChartData);
        } else {
          console.error('Fetched insights data is not an array:', insights);
        }
      } catch (error) {
        console.error('Error fetching insights data:', error.message);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (chartContainerRef.current) {
      // Destroy previous chart instance if it exists
      if (chartInstanceRef.current) {
        chartInstanceRef.current.destroy();
      }

      // Create new chart instance
      chartInstanceRef.current = new Chart(chartContainerRef.current, {
        type: 'bar',
        data: chartData,
        options: {
          responsive: true,
          scales: {
            y: { beginAtZero: true }
          }
        }
      });
    }
  }, [chartData]);

  return (
    <Container>
      <Paper elevation={3} style={{ padding: '20px' }}>
        <Typography variant="h5" component="h2">
          Sector Intensity
        </Typography>
        {chartData.labels ? (
          <canvas ref={chartContainerRef} />
        ) : (
          <Typography variant="h6" component="p">
            Loading data...
          </Typography>
        )}
      </Paper>
    </Container>
  );
};

export default InsightsChart;
