import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import { Container, Paper, Typography } from '@mui/material';
import './InsightsChart.css'; // Assuming you have a CSS file for styling

const InsightsChart = () => {
  const [chartData, setChartData] = useState({});
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/insights');
        const insights = response.data;

        if (Array.isArray(insights)) {
          const sectors = insights.map(insight => insight.sector);
          const intensities = insights.map(insight => insight.intensity);

          setChartData({
            labels: sectors,
            datasets: [
              {
                label: 'Intensity',
                data: intensities,
                backgroundColor: 'rgba(75, 192, 192, 0.6)',
              }
            ]
          });
        } else {
          setError('Fetched data is not in expected format');
        }
      } catch (error) {
        setError('Error fetching insights data: ' + error.message);
      }
    };

    fetchData();
  }, []);

  return (
    <Container>
      <Paper elevation={3} style={{ padding: '20px' }}>
        <Typography variant="h5" component="h2">
          Sector Intensity
        </Typography>
        {error ? (
          <Typography variant="h6" component="p" color="error">
            {error}
          </Typography>
        ) : (
          <Bar
            data={chartData}
            options={{
              responsive: true,
              scales: {
                y: { beginAtZero: true }
              }
            }}
          />
        )}
      </Paper>
    </Container>
  );
};

export default InsightsChart;
