import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import { Container, Paper, Typography, CircularProgress } from '@mui/material'; // Import CircularProgress
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import './InsightsChart.css'; // Assuming you have a CSS file for styling

// Register necessary components with Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const InsightsChart = () => {
  const [chartData, setChartData] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/insights');
        const insights = response.data;

        if (Array.isArray(insights)) {
          // Aggregate data by sector
          const sectorIntensityMap = insights.reduce((acc, insight) => {
            if (!acc[insight.sector]) {
              acc[insight.sector] = 0;
            }
            acc[insight.sector] += insight.intensity;
            return acc;
          }, {});

          const sectors = Object.keys(sectorIntensityMap);
          const intensities = Object.values(sectorIntensityMap);

          setChartData({
            labels: sectors,
            datasets: [
              {
                label: 'Intensity',
                data: intensities,
                backgroundColor: 'rgba(75, 192, 192, 0.6)',
              }
            ]
          });
        } else {
          console.error('Fetched insights data is not an array:', insights);
        }
      } catch (error) {
        console.error('Error fetching insights data:', error.message);
      }
    };

    fetchData();
  }, []);

  return (
    <Container className="chart-container">
      <Paper elevation={3} className="chart-paper">
        <Typography variant="h5" component="h2" >
          Sector Intensity
        </Typography>
        {chartData.labels ? (
          <Bar
            data={chartData}
            options={{
              responsive: true,
              scales: {
                y: { beginAtZero: true }
              }
            }}
          />
        ) : (
          <div className="loading-container">
            <CircularProgress /> {/* Render CircularProgress while loading */}
          </div>
        )}
      </Paper>
    </Container>
  );
};

export default InsightsChart;
