// src/components/InsightsChart.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import { Container, Paper, Typography } from '@mui/material';
import './InsightsChart.css';

const InsightsChart = () => {
  const [chartData, setChartData] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get('http://localhost:5000/insights');
      const insights = response.data;

      const sectors = insights.map(insight => insight.sector);
      const intensities = insights.map(insight => insight.intensity);

      setChartData({
        labels: sectors,
        datasets: [
          {
            label: 'Intensity',
            data: intensities,
            backgroundColor: 'rgba(75, 192, 192, 0.6)',
          }
        ]
      });
    };

    fetchData();
  }, []);

  return (
    <Container className="container">
      <Paper elevation={3} className="paper">
        <Typography variant="h5" component="h2" className="title">
          Sector Intensity
        </Typography>
        <div className="bar-chart">
          <Bar 
            data={chartData}
            options={{
              responsive: true,
              scales: {
                y: { beginAtZero: true }
              }
            }}
          />
        </div>
      </Paper>
    </Container>
  );
};

export default InsightsChart;
